/**
 *
 * The MessageProvider class stores header messages for the application.
 * It also exposes a few methods to grab a random message to display for
 * each new message created.
 *
 */

MessageProvider = function() {};

MessageProvider.prototype.messages =
[
	'Filthy Sandraker.',
	'Prepare yourself!',
	'Victory will soon be ours!',
	'Prepare for deployment!',
	'No mercy this day.',
	'No quarter given.'
];

/**
 * Grab a random message from the messages collection.
 */
MessageProvider.prototype.getMessage = function()
{
	var rnd = Math.floor(Math.random() * (this.messages.length));
	return this.messages[rnd];
};

exports.MessageProvider = MessageProvider;
